import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import { Icon } from "@iconify/react";
import { redirect } from "react-router-dom";
import { useState, useRef, useEffect } from "react";
import "./Doctors.css";

const Doctors = () => {
  const doctors = [
    {
      id: 1,
      name: "Dr. John Doe",
      rating: 4.8,
      post: "Cardiologist",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit...",
      image:
        "https://img.freepik.com/free-photo/bearded-doctor-glasses_23-2147896187.jpg",
    },
    {
      id: 1,
      name: "Dr. John Doe",
      rating: 4.8,
      post: "Cardiologist",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit...",
      image:
        "https://img.freepik.com/free-photo/female-doctor-hospital-with-stethoscope_23-2148827774.jpg",
    },
    {
      id: 1,
      name: "Dr. John Doe",
      rating: 4.8,
      post: "Cardiologist",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit...",
      image:
        "https://img.freepik.com/free-photo/doctor-with-his-arms-crossed-white-background_1368-5790.jpg?size=338&ext=jpg&ga=GA1.1.1413502914.1696809600&semt=sph",
    },
    {
      id: 1,
      name: "Dr. John Doe",
      rating: 4.8,
      post: "Cardiologist",
      description: "Lorem ipsum dolor sit amet, consectetur adipiscing elit...",
      image:
        "https://img.freepik.com/free-photo/doctor-with-his-arms-crossed-white-background_1368-5790.jpg?size=338&ext=jpg&ga=GA1.1.1413502914.1696809600&semt=sph",
    },
    // Add more doctors here
  ];

  const [showMore, setShowMore] = useState(false);
  const visibleCards = showMore ? doctors : doctors.slice(0, 3);

  const toggleShowMore = () => {
    if (!showMore) {
      // Scroll to the bottom of the page when showMore is true
      window.scrollTo(0, document.body.scrollHeight - 1300);
    }
    setShowMore(!showMore);
  };
  const viewMoreButtonRef = useRef(null);

  return (
    <Container id="doctors">
      <h2>Meet Our Doctors</h2>
      <Row>
        {visibleCards.map((doctor) => (
          <Col md={4} key={doctor.id}>
            <Card>
              <div className="card">
                <Card.Img
                  variant="top"
                  src={doctor.image}
                  alt={doctor.name}
                  className="image-card"
                />
              </div>
              <Card.Body>
                <Card.Title>{doctor.name}</Card.Title>
                <Card.Subtitle className="mb-2 text-muted">
                  {doctor.post}
                </Card.Subtitle>
                <Card.Text>{doctor.description}</Card.Text>
                <div className="d-flex justify-content-between">
                  <span className="">
                    <span
                      style={{
                        marginRight: 10,
                        marginTop: 50,
                      }}
                    >
                      <Icon
                        icon="ic:round-star"
                        color="orange"
                        width="25"
                        height="25"
                      />
                    </span>
                    {doctor.rating}
                  </span>
                  <button
                    className="btn btn-primary"
                    onClick={() => {
                      window.location.href = "/appointment";
                    }}
                  >
                    Book Appointment
                  </button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      {doctors.length > 3 && (
        <button
          ref={viewMoreButtonRef}
          onClick={(e) => {
            e.preventDefault();
            toggleShowMore();
          }}
        >
          {showMore ? "View Less" : "View More"}
        </button>
      )}
    </Container>
  );
};
export default Doctors;
