import React, { useState } from "react";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./Appointment.css";

const AppointmentForm = () => {
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    date: "",
    time: "",
    problemType: "",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // You can add your logic to handle the form submission here.
    console.log(formData);
    // Reset the form fields after submission (optional).
    toast.success("Appointment booked successfully!");
    setFormData({
      name: "",
      email: "",
      date: "",
      time: "",
      problemType: "",
    });
  };

  return (
    <div>
      <ToastContainer
        position="top-right"
        autoClose={5000} // 5 seconds
        closeOnClick={true}
        pauseOnHover={true}
        limit={5}
        rtl={false}
        className="custom-toast-container"
      />
      <a href="/">
        {" "}
        <img
          src="https://consultme.bg/wp-content/uploads/2018/11/Original_png-1-e1542727565346.png"
          height="80px"
        />
      </a>

      <form onSubmit={handleSubmit}>
        <h2>Book Appointment</h2>
        <div className="form-group">
          <label htmlFor="name">Name:</label>
          <input
            type="text"
            id="name"
            name="name"
            value={formData.name}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="email">Email:</label>
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="date">Date:</label>
          <input
            type="date"
            id="date"
            name="date"
            value={formData.date}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="time">Time:</label>
          <input
            type="time"
            id="time"
            name="time"
            value={formData.time}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label htmlFor="problemType">Problem Type:</label>
          <select
            id="problemType"
            name="problemType"
            value={formData.problemType}
            onChange={handleChange}
            required
          >
            <option value="">Select Problem Type</option>
            <option value="General Checkup">General Checkup</option>
            <option value="Dental">Dental</option>
            <option value="Orthopedic">Heart Disease</option>
            <option value="Orthopedic">Others</option>
            {/* Add more options as needed */}
          </select>
        </div>

        <button type="submit">Book Appointment</button>
      </form>
    </div>
  );
};

export default AppointmentForm;
